import './card.css'
import Aws from '/public/icons/aws.svg'
import { CSSProperties, useEffect, useRef, useState } from "react";

interface CSSMousePos extends CSSProperties {
  '--x': number;
  '--y': number;
}

interface MousePos {
  x: number;
  y: number;
}

const Card = ({children}) => {
  const [localMousePos, setLocalMousePos] = useState<MousePos>({ x: 0, y: 0 });
  const card = useRef(null);

  useEffect(() => {
    const handleMouseMove = (event: MouseEvent) => {
      const rect = card.current.getBoundingClientRect();
      const localX = event.clientX - rect.left;
      const localY = event.clientY - rect.top;
      setLocalMousePos({ x: localX, y: localY });
    };
    document.addEventListener('mousemove', handleMouseMove);
    return () => {
      document.removeEventListener(
        'mousemove',
        handleMouseMove
      );
    };
  }, [])

  return (
      <div
        className='flex w-full flex-col items-center gap-4 rounded-xl text-sm sm:text-lg'
        style={{ '--x': localMousePos.x, '--y': localMousePos.y } as CSSMousePos}
        ref={card}
      >
        {children}
      </div>
  )
}

function Icon({ children, ...props }) {
  return <div {...props} className='w-16 text-center text-white sm:w-24'>{children}</div>
}

